mapper.select:
select

FROM content_sys_msg
WHERE
is_disabled=0

  AND msg_title=#{title,jdbcType=VARCHAR}


  AND relate_type=#{type,jdbcType=INTEGER}


  AND msg_pushtime >= #{reciveStart,jdbcType=TIMESTAMP}


  AND msg_pushtime <= #{reciveEnd,jdbcType=TIMESTAMP}

limit #{start,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
