mapper.select:
        select

        from payment_order
        where status = #{status}

          and pay_channel = #{payChannel}

				and trade_type in

	            #{item}


          and DATE_FORMAT(end_at, '%Y-%m-%d') = DATE_FORMAT(#{endAt}, '%Y-%m-%d')
          and source in(3,4)

select
po.id, order_no,order_type,order_price,pay_no,pay_amount,
status,trade_type,po.create_at,
end_at,pay_channel,out_account,in_account,order_title,shop_id,draw_user_id,pay_name,batch_no,refund_rejected,weixin_mch_id,
ali_seller_id,wechat_mch_id,ry_mch_id,ry_app_id,source,org_channel,pay_scene,mch_info_id,store_id,pos_no,poe.memo
from payment_order po
left join payment_order_ext poe on po.id = poe.payment_order_id
where source in (3,4)

        and order_no = #{orderNo,jdbcType=VARCHAR}


        and pay_channel = #{payChannel,jdbcType=INTEGER}


        and pay_channel in (0,1,2,3)


        and payer_recon_flag = #{payerReconflag,jdbcType=INTEGER}


        and ry_mch_id = #{ryMchId,jdbcType=VARCHAR}


        and trade_type = #{tradeType,jdbcType=INTEGER}


        and trade_type in (0,1)


        and status = #{status,jdbcType=INTEGER}


        and status = 2


        and pay_scene = #{payScene}


        and end_at >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%s')


        and end_at <= STR_TO_DATE(#{endTime,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%s')

order by po.create_at desc

    limit #{startIndex},#{pageSize}

select  count(*)
from payment_order
where source in (3,4)

    and order_no = #{orderNo,jdbcType=VARCHAR}


    and pay_channel = #{payChannel,jdbcType=INTEGER}


        and pay_channel in (0,1,2,3)


    and payer_recon_flag = #{payerReconflag,jdbcType=INTEGER}


    and trade_type = #{tradeType,jdbcType=INTEGER}


    and trade_type in (0,1)


    and status = #{status,jdbcType=INTEGER}


    and status = 2


    and ry_mch_id = #{ryMchId,jdbcType=VARCHAR}


    and pay_scene = #{payScene}


    and end_at >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%s')


