mapper.insert:
insert into weixin_mch
app_id, `key`, mch_id, cret_path, public_code, user_id, trade_type, is_rongyi_pay
#{appId,jdbcType=VARCHAR}, #{key,jdbcType=VARCHAR}, #{mchId,jdbcType=VARCHAR}, #{cretPath,jdbcType=VARCHAR}, #{publicCode,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{tradeType,jdbcType=VARCHAR}, #{isRongyiPay,jdbcType=TINYINT}

mapper.select:
select

from weixin_mch
where user_id = #{userId,jdbcType=VARCHAR}

   and public_code = #{publicCode,jdbcType=VARCHAR}

select

from weixin_mch
where app_id = #{appId,jdbcType=VARCHAR}

  and trade_type like  CONCAT('%',#{tradeType},'%')


  and user_id = #{userId,jdbcType=VARCHAR}
