mapper.select:
select cs.shop_id shopId, cs.account_list accountList,
       pbc.biz_real_address bizRealAddress, pbc.biz_name bizName, pbc.link_shop_op linkShopOp, pbc.link_role linkRole,link_type linkType
from config_shop cs join payment_bill_config pbc on cs.config_id = pbc.id


    cs.config_id = #{configId}



  limit #{startRecord}, #{pageSize, jdbcType=INTEGER}

select count(1)
from config_shop cs join payment_bill_config pbc on cs.config_id = pbc.id


    cs.config_id = #{configId}
