mapper.select:
SELECT of.id, of.order_no, of.weidian_id shop_id, of.total_amount, sum(odf.quantity) totalQuantity, mc.id mall_id, oe.create_at finishTime, mc.mall_name, si.shop_name, 1 orderType FROM ry_osm.order_form of INNER JOIN ry_osm.order_event oe ON of.order_no = oe.order_no AND oe.type = 19 INNER JOIN ry_osm.order_detail_form odf ON of.order_no = odf.order_no INNER JOIN ry_rmmm.shop_info si on si.id = of.weidian_id LEFT JOIN ry_rmmm.mall_cooperate mc on mc.id = si.mall_id WHERE of.order_source IN (0, 2) AND oe.create_at >= DATE_ADD(#{accountDate},INTERVAL -1 DAY) AND oe.create_at < #{accountDate} And EXISTS (select 1 from weixin_mch wm where of.weixin_app_id = wm.app_id And wm.is_rongyi_pay = 0)
and mc.id = #{mallId}
and si.id = #{shopId}
GROUP BY of.id, of.order_no ORDER BY oe.create_at DESC

SELECT tor.id orderId, tor.order_no, tso.id subOrderId, tso.unit_price, tso.unit_count, tuo.coupon_id, tuo.mall_id mallMid, tuo.shop_id shopMid, tuo.use_time finishTime, mc.id mall_id, mc.mall_name, si.id shop_id, si.shop_name, 2 orderType FROM ry_trade.trade_order tor INNER JOIN ry_trade.trade_sub_order tso ON tor.order_no = tso.order_no INNER JOIN ry_trade.trade_user_code tuo ON tor.id = tuo.order_id AND tso.id = tuo.sub_order_id INNER JOIN ry_rmmm.shop_info si on si.shop_mid = tuo.shop_id LEFT JOIN ry_rmmm.mall_cooperate mc on mc.mall_mid = si.mall_mid WHERE tor.source IN (1, 2) AND tuo.STATUS = 2 AND tuo.use_time >= DATE_ADD(#{accountDate},INTERVAL -1 DAY) AND tuo.use_time < #{accountDate} AND TRIM(tuo.shop_id) != "" And EXISTS (select 1 from weixin_mch wm where tor.weixin_app_id = wm.app_id And wm.is_rongyi_pay = 0)
and mc.id = #{mallId}
and si.id = #{shopId}
ORDER BY tuo.use_time DESC
