			and seller_name like #{sellerName,jdbcType=VARCHAR}
		
		
			and seller_account like #{sellerAccount,jdbcType=VARCHAR}
		
		
			and pic_upload_at >= #{uploadStart,jdbcType=TIMESTAMP}
		
		
