			and commission_amount <= #{amountEnd,jdbcType=DECIMAL}
		
		
			and status in
			
				#{item}
			
		
		order by ${orderBy}
		limit
		#{begin,jdbcType=INTEGER},#{size,jdbcType=INTEGER}

